package com.syntax.Class007;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		while (i>=3) {
			System.out.println("Hello");
		i++;
		
		}
		
		System.out.println("do while");
	
	int j=1;
	
	do {
		
	System.out.println("Hello");
	j++;
	
	}while(j>=3);
	
	
	
	System.out.println("---------------------------");
	
	int n=1;
	
	do {
	System.out.println(n);
	n++;
	}while (n<=10);
	
	
	}

}
